mapper.update:
update tb_item_param_item
set item_id = #{itemId,jdbcType=BIGINT},
  created = #{created,jdbcType=TIMESTAMP},
  updated = #{updated,jdbcType=TIMESTAMP},
  param_data = #{paramData,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=BIGINT}

update tb_item_param_item
set item_id = #{itemId,jdbcType=BIGINT},
  created = #{created,jdbcType=TIMESTAMP},
  updated = #{updated,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT p.id, p.item_id itemId, p.param_data paramData, p.created, p.updated, i.title itemName FROM tb_item_param_item p LEFT JOIN tb_item i on p.item_id=i.id where 1=1
and p.item_id=#{itemId}
and i.title LIKE CONCAT(CONCAT("%",#{itemName},"%"))
